import React, { useState } from 'react';
import Modal from '../UI/Modal';

const statusOptions = [
  { value: 'incomplete', label: 'Incomplete' },
  { value: 'in_progress', label: 'In Progress' },
  { value: 'completed', label: 'Completed' }
];

const AcknowledgmentModal = ({ open, onClose, onSubmit, task }) => {
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState(task.status);
  const [error, setError] = useState('');

  const handleSubmit = () => {
    if (!description.trim()) {
      setError('Description is required.');
      return;
    }
    setError('');
    onSubmit({ description, status });
  };

  return (
    <Modal open={open} onClose={onClose}>
      <div style={{ padding: 24, minWidth: 320 }}>
        <h2>Acknowledge Task</h2>
        <div style={{ marginBottom: 16 }}>
          <label>Description</label>
          <textarea
            value={description}
            onChange={e => setDescription(e.target.value)}
            rows={3}
            style={{ width: '100%', marginTop: 8 }}
            placeholder="Add your acknowledgment..."
          />
        </div>
        <div style={{ marginBottom: 16 }}>
          <label>Change Status</label>
          <select
            value={status}
            onChange={e => setStatus(e.target.value)}
            style={{ width: '100%', marginTop: 8 }}
          >
            {statusOptions.map(opt => (
              <option key={opt.value} value={opt.value}>{opt.label}</option>
            ))}
          </select>
        </div>
        {error && <div style={{ color: 'red', marginBottom: 8 }}>{error}</div>}
        <div style={{ display: 'flex', justifyContent: 'flex-end', gap: 8 }}>
          <button onClick={onClose} style={{ padding: '8px 16px' }}>Cancel</button>
          <button onClick={handleSubmit} style={{ padding: '8px 16px', background: '#007bff', color: 'white', border: 'none', borderRadius: 4 }}>Acknowledge</button>
        </div>
      </div>
    </Modal>
  );
};

export default AcknowledgmentModal;
