import React from 'react';
import { Link } from 'react-router-dom';
import { format } from 'date-fns';
import './HandoverItem.css';

const HandoverItem = ({ handover, currentUser }) => {
  const getPriorityClass = () => {
    switch (handover.priority) {
      case 'low': return 'priority-low';
      case 'medium': return 'priority-medium';
      case 'high': return 'priority-high';
      case 'critical': return 'priority-critical';
      default: return '';
    }
  };

  const getStatusClass = () => {
    switch (handover.status) {
      case 'pending': return 'status-pending';
      case 'in-progress': return 'status-in-progress';
      case 'completed': return 'status-completed';
      default: return '';
    }
  };

  return (
    <div className={`handover-item ${getPriorityClass()}`}>
      <div className="handover-header">
        <h3>
          <Link to={`/handover/${handover.id}`}>{handover.title}</Link>
        </h3>
        <span className={`status-badge ${getStatusClass()}`}>
          {handover.status.replace('-', ' ')}
        </span>
      </div>
      
      <div className="handover-meta">
        <div className="meta-item">
          <span className="meta-label">From:</span>
          <span>{handover.fromShift.name} ({handover.fromShift.time})</span>
        </div>
        <div className="meta-item">
          <span className="meta-label">To:</span>
          <span>{handover.toShift.name} ({handover.toShift.time})</span>
        </div>
        <div className="meta-item">
          <span className="meta-label">Created:</span>
          <span>{format(new Date(handover.createdAt), 'MMM d, yyyy h:mm a')}</span>
        </div>
        <div className="meta-item">
          <span className="meta-label">By:</span>
          <span>{handover.createdBy.name}</span>
        </div>
      </div>
      
      <div className="handover-description">
        <p>{handover.description}</p>
      </div>
      
      {handover.attachments.length > 0 && (
        <div className="handover-attachments">
          <span className="attachment-icon"></span>
          <span>{handover.attachments.length} attachment(s)</span>
        </div>
      )}
      
      <div className="handover-actions">
        <Link to={`/handover/${handover.id}`} className="view-btn">
          View Details
        </Link>
        {currentUser.id === handover.createdBy.id && (
          <button className="edit-btn">Edit</button>
        )}
      </div>
    </div>
  );
};

export default HandoverItem;
