import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import HandoverList from './components/Handovers/HandoverList';
import CreateHandover from './components/Handovers/CreateHandover';
import HandoverDetail from './components/Handovers/HandoverDetail';
import ShiftManager from './components/Shifts/ShiftManager';
import HandoverReports from './components/Reports/HandoverReports';
import Header from './components/UI/Header';
import Sidebar from './components/UI/Sidebar';
import './App.css';

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [handovers, setHandovers] = useState([]);
  const [shifts, setShifts] = useState([]);

  // Load dummy data
  React.useEffect(() => {
    const loadDummyData = async () => {
      const { dummyHandovers } = await import('./data/dummyHandovers');
      const { dummyShifts } = await import('./data/dummyShifts');
      setHandovers(dummyHandovers);
      setShifts(dummyShifts);
    };
    loadDummyData();
  }, []);

  const handleLogin = (user) => {
    setCurrentUser(user);
  };

  const handleLogout = () => {
    setCurrentUser(null);
  };

  const addHandover = (newHandover) => {
    setHandovers([{
      ...newHandover,
      id: `handover-${Date.now()}`,
      createdAt: new Date().toISOString(),
      createdBy: currentUser
    }, ...handovers]);
  };

  return (
    <Router>
      <div className="app-container">
        <Header currentUser={currentUser} onLogout={handleLogout} />
        <div className="main-content">
          {currentUser && <Sidebar />}
          <div className="content-area">
            <Routes>
              <Route path="/login" element={<Login onLogin={handleLogin} />} />
              <Route path="/register" element={<Register />} />
              <Route path="/" element={
                currentUser ? 
                  <HandoverList handovers={handovers} currentUser={currentUser} /> : 
                  <Login onLogin={handleLogin} />
              } />
              <Route path="/create" element={
                currentUser ? 
                  <CreateHandover shifts={shifts} onSubmit={addHandover} /> : 
                  <Login onLogin={handleLogin} />
              } />
              <Route path="/handover/:id" element={
                currentUser ? 
                  <HandoverDetail handovers={handovers} /> : 
                  <Login onLogin={handleLogin} />
              } />
              <Route path="/shifts" element={
                currentUser ? 
                  <ShiftManager shifts={shifts} setShifts={setShifts} /> : 
                  <Login onLogin={handleLogin} />
              } />
              <Route path="/reports" element={
                currentUser ? 
                  <HandoverReports handovers={handovers} /> : 
                  <Login onLogin={handleLogin} />
              } />
            </Routes>
          </div>
        </div>
      </div>
    </Router>
  );
}

export default App;
