export const dummyHandovers = [
  {
    id: '1',
    title: 'Equipment Maintenance',
    description: 'Generator #3 needs routine maintenance before next shift. Check oil levels and coolant.',
    fromShift: {
      id: '3',
      name: 'Night Shift',
      time: '22:00 - 06:00'
    },
    toShift: {
      id: '1',
      name: 'Morning Shift',
      time: '06:00 - 14:00'
    },
    createdBy: {
      id: 'user2',
      name: 'Sarah Johnson'
    },
    createdAt: '2025-08-04T22:30:00Z',
    tasks: [
      {
        description: 'Check oil levels in Generator #3',
        status: 'completed',
        createdAt: '2025-08-04T22:30:00Z',
        lastUpdated: '2025-08-04T23:15:00Z'
      },
      {
        description: 'Inspect coolant system',
        status: 'in-progress',
        createdAt: '2025-08-04T22:30:00Z',
        lastUpdated: '2025-08-04T23:30:00Z'
      },
      {
        description: 'Document maintenance in logbook',
        status: 'pending',
        createdAt: '2025-08-04T22:30:00Z'
      }
    ],
    attachments: []
  },
  {
    id: '2',
    title: 'Inventory Update',
    description: 'Received 50 new units today. System has been updated but physical count needed tomorrow.',
    priority: 'medium',
    status: 'pending',
    fromShift: {
      id: '1',
      name: 'Morning Shift',
      time: '06:00 - 14:00'
    },
    toShift: {
      id: '2',
      name: 'Afternoon Shift',
      time: '14:00 - 22:00'
    },
    createdBy: {
      id: 'user1',
      name: 'John Smith'
    },
    createdAt: '2025-08-04T13:45:00Z',
    tasks: [
      {
        description: 'Update inventory system',
        status: 'completed',
        createdAt: '2025-08-04T13:45:00Z',
        lastUpdated: '2025-08-04T14:30:00Z'
      },
      {
        description: 'Perform physical count of new units',
        status: 'pending',
        createdAt: '2025-08-04T13:45:00Z'
      },
      {
        description: 'File delivery documentation',
        status: 'pending',
        createdAt: '2025-08-04T13:45:00Z'
      }
    ],
    attachments: [
      { name: 'inventory_list.pdf', type: 'pdf' },
      { name: 'delivery_photo.jpg', type: 'image' }
    ]
  },
  {
    id: '3',
    title: 'Safety Inspection',
    description: 'Quarterly safety inspection completed. Two issues found and tagged for repair.',
    priority: 'critical',
    status: 'completed',
    fromShift: {
      id: '2',
      name: 'Afternoon Shift',
      time: '14:00 - 22:00'
    },
    toShift: {
      id: '3',
      name: 'Night Shift',
      time: '22:00 - 06:00'
    },
    createdBy: {
      id: 'user3',
      name: 'Mike Brown'
    },
    createdAt: '2025-08-04T21:15:00Z',
    tasks: [
      {
        description: 'Complete quarterly safety inspection checklist',
        status: 'completed',
        createdAt: '2025-08-04T21:15:00Z',
        lastUpdated: '2025-08-04T22:00:00Z'
