import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import HandoverItem from './HandoverItem';
import './HandoverList.css';

const HandoverList = ({ handovers = [], onHandoversUpdate }) => {
  const navigate = useNavigate();
  const [statusFilter, setStatusFilter] = useState('all');
  const [priorityFilter, setPriorityFilter] = useState('all');

  // Calculate task statistics
  const taskStats = {
    priority: {
      low: 0,
      medium: 0,
      high: 0,
      critical: 0
    },
    status: {
      pending: 0,
      'in-progress': 0,
      completed: 0
    }
  };
  
  handovers.forEach(handover => {
    handover.tasks?.forEach(task => {
      if (task.status in taskStats.status) {
        taskStats.status[task.status]++;
      }
      if (task.priority in taskStats.priority) {
        taskStats.priority[task.priority]++;
      }
    });
  });

  const handleTaskUpdate = (handoverId, updatedTasks) => {
    const updatedHandovers = handovers.map(handover => {
      if (handover.id === handoverId) {
        return {
          ...handover,
          tasks: updatedTasks
        };
      }
      return handover;
    });
    
    if (typeof onHandoversUpdate === 'function') {
      onHandoversUpdate(updatedHandovers);
    } else {
      console.warn('onHandoversUpdate function not provided to HandoverList component');
    }
  };

  const filteredHandovers = handovers.filter(handover => {
    if (statusFilter === 'all' && priorityFilter === 'all') return true;
    
    return handover.tasks?.some(task => {
      const statusMatch = statusFilter === 'all' || task.status === statusFilter;
      const priorityMatch = priorityFilter === 'all' || task.priority === priorityFilter;
      return statusMatch && priorityMatch;
    });
  });

  return (
    <div className="handover-container">
      <div className="handover-header">
        <h2>Shift Handovers</h2>
        <button 
          className="create-handover-btn"
          onClick={() => navigate('/create')}
        >
          Create New Handover
        </button>
      </div>

      <div className="task-statistics">
        <div className="stats-section">
          <h3>Status Statistics</h3>
          <div className="stats-grid">
            <div className="stat-card pending">
              <h4>Pending Tasks</h4>
              <span className="stat-number">{taskStats.status.pending || 0}</span>
            </div>
            <div className="stat-card in-progress">
              <h4>In Progress</h4>
              <span className="stat-number">{taskStats.status['in-progress'] || 0}</span>
            </div>
            <div className="stat-card completed">
              <h4>Completed</h4>
              <span className="stat-number">{taskStats.status.completed || 0}</span>
            </div>
          </div>
        </div>

        <div className="stats-section">
          <h3>Priority Statistics</h3>
          <div className="stats-grid">
            <div className="stat-card priority-low">
              <h4>Low Priority</h4>
              <span className="stat-number">{taskStats.priority.low || 0}</span>
            </div>
            <div className="stat-card priority-medium">
              <h4>Medium Priority</h4>
              <span className="stat-number">{taskStats.priority.medium || 0}</span>
            </div>
            <div className="stat-card priority-high">
              <h4>High Priority</h4>
              <span className="stat-number">{taskStats.priority.high || 0}</span>
            </div>
            <div className="stat-card priority-critical">
              <h4>Critical Priority</h4>
              <span className="stat-number">{taskStats.priority.critical || 0}</span>
            </div>
          </div>
        </div>
      </div>

      <div className="filters">
        <div className="filter-group">
          <label>Status:</label>
          <select 
            value={statusFilter} 
            onChange={(e) => setStatusFilter(e.target.value)}
          >
            <option value="all">All Tasks</option>
            <option value="pending">Pending Tasks</option>
            <option value="in-progress">In Progress Tasks</option>
            <option value="completed">Completed Tasks</option>
          </select>
        </div>
        <div className="filter-group">
          <label>Task Priority:</label>
          <select 
            value={priorityFilter} 
            onChange={(e) => setPriorityFilter(e.target.value)}
          >
            <option value="all">All Priorities</option>
            <option value="low">Low Priority</option>
            <option value="medium">Medium Priority</option>
            <option value="high">High Priority</option>
            <option value="critical">Critical Priority</option>
          </select>
        </div>
      </div>
      
      <div className="view-options">
        <button className="view-btn active">List View</button>
        <button className="view-btn">Board View</button>
      </div>

      <div className="handover-list">
        {filteredHandovers.length > 0 ? (
          filteredHandovers.map((handover) => (
            <HandoverItem
              key={handover.id}
              handover={{
                ...handover,
                onTaskUpdate: handleTaskUpdate
              }}
              onClick={() => navigate(`/handover/${handover.id}`)}
            />
          ))
        ) : (
          <div className="no-handovers">
            <p>No handovers found matching the selected filters.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default HandoverList;
