import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import ShiftSelector from '../Shifts/ShiftSelector';
import Modal from '../UI/Modal';
import './CreateHandover.css';

const CreateHandover = ({ shifts, onSubmit }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    priority: 'medium',
    status: 'pending',
    fromShift: null,
    toShift: null,
    attachments: [],
    tasks: []
  });
  const [showShiftModal, setShowShiftModal] = useState(false);
  const [shiftType, setShiftType] = useState('');
  const [newTask, setNewTask] = useState('');
  const [newTaskStatus, setNewTaskStatus] = useState('pending');
  const [success, setSuccess] = useState(false);
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleFileUpload = (e) => {
    const files = Array.from(e.target.files);
    setFormData({
      ...formData,
      attachments: [...formData.attachments, ...files.map(file => ({
        name: file.name,
        type: file.type.split('/')[1] || 'file'
      }))]
    });
  };

  const handleShiftSelect = (shift) => {
    if (shiftType === 'from') {
      setFormData({ ...formData, fromShift: shift });
    } else {
      setFormData({ ...formData, toShift: shift });
    }
    setShowShiftModal(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
    setSuccess(true);
    setTimeout(() => {
      navigate('/');
    }, 1500);
  };

  return (
    <div className="create-handover-container">
      <h2>Create New Shift Handover</h2>
      
      {success && (
        <div className="alert success">
          Handover created successfully! Redirecting...
        </div>
      )}
      
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Title*</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleInputChange}
            required
          />
        </div>
        
        <div className="form-group">
          <label>Description*</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            required
          />
        </div>
        
        <div className="form-row">
          <div className="form-group">
            <label>Priority*</label>
            <select
              name="priority"
              value={formData.priority}
              onChange={handleInputChange}
              required
            >
              <option value="low">Low</option>
              <option value="medium">Medium</option>
              <option value="high">High</option>
              <option value="critical">Critical</option>
            </select>
          </div>
          
          <div className="form-group">
            <label>Status*</label>
            <select
              name="status"
              value={formData.status}
              onChange={handleInputChange}
              required
            >
              <option value="pending">Pending</option>
              <option value="in-progress">In Progress</option>
              <option value="completed">Completed</option>
            </select>
          </div>
        </div>
        
        <div className="form-row">
          <div className="form-group">
            <label>From Shift*</label>
            <div className="shift-selector">
              <input
                type="text"
                value={formData.fromShift?.name || ''}
                readOnly
                placeholder="Select shift"
                required
              />
              <button 
                type="button"
                className="select-shift-btn"
                onClick={() => {
                  setShiftType('from');
                  setShowShiftModal(true);
                }}
              >
                Select
              </button>
            </div>
          </div>
          
          <div className="form-group">
            <label>To Shift*</label>
            <div className="shift-selector">
              <input
                type="text"
                value={formData.toShift?.name || ''}
                readOnly
                placeholder="Select shift"
                required
              />
              <button 
                type="button"
                className="select-shift-btn"
                onClick={() => {
                  setShiftType('to');
                  setShowShiftModal(true);
                }}
              >
                Select
              </button>
            </div>
          </div>
        </div>
        
        <div className="form-group">
          <label>Attachments</label>
          <input
            type="file"
            onChange={handleFileUpload}
            multiple
          />
          {formData.attachments.length > 0 && (
            <div className="attachments-preview">
              <p>Files to upload:</p>
              <ul>
                {formData.attachments.map((file, index) => (
                  <li key={index}>
                    <span className={`file-icon ${file.type}`}></span>
                    {file.name}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>

        <div className="form-group">
          <label>Tasks</label>
          <div className="task-input-container">
            <input
              type="text"
              value={newTask}
              onChange={(e) => setNewTask(e.target.value)}
              placeholder="Enter a task"
              className="task-input"
            />
            <select
              value={newTaskStatus}
              onChange={(e) => setNewTaskStatus(e.target.value)}
              className="task-status-select"
            >
              <option value="pending">Pending</option>
              <option value="in-progress">In Progress</option>
              <option value="completed">Completed</option>
            </select>
            <button
              type="button"
              className="add-task-btn"
              onClick={() => {
                if (newTask.trim()) {
                  setFormData({
                    ...formData,
                    tasks: [...formData.tasks, {
                      description: newTask.trim(),
                      status: newTaskStatus
                    }]
                  });
                  setNewTask('');
                  setNewTaskStatus('pending');
                }
              }}
            >
              Add Task
            </button>
          </div>
          {formData.tasks.length > 0 && (
            <div className="tasks-table-container">
              <table className="tasks-table">
                <thead>
                  <tr>
                    <th>Task Description</th>
                    <th>Status</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {formData.tasks.map((task, index) => (
                    <tr key={index}>
                      <td>{task.description}</td>
                      <td>
                        <select
                          value={task.status}
                          onChange={(e) => {
                            const updatedTasks = [...formData.tasks];
                            updatedTasks[index] = {
                              ...task,
                              status: e.target.value
                            };
                            setFormData({
                              ...formData,
                              tasks: updatedTasks
                            });
                          }}
                          className={`status-badge ${task.status}`}
                        >
                          <option value="pending">Pending</option>
                          <option value="in-progress">In Progress</option>
                          <option value="completed">Completed</option>
                        </select>
                      </td>
                      <td>
                        <button
                          type="button"
                          className="remove-task-btn"
                          onClick={() => {
                            setFormData({
                              ...formData,
                              tasks: formData.tasks.filter((_, i) => i !== index)
                            });
                          }}
                        >
                          Ã—
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
        
        <div className="form-actions">
          <button 
            type="submit" 
            className="submit-btn"
          >
            Submit Handover
          </button>
        </div>
      </form>
      
      <Modal show={showShiftModal} onClose={() => setShowShiftModal(false)}>
        <ShiftSelector 
          shifts={shifts} 
          onSelect={handleShiftSelect} 
        />
      </Modal>
    </div>
  );
};

export default CreateHandover;
